import AddDocument from "./commands/AddDocument";
import RemoveDocument from "./commands/RemoveDocument";
import UpdateDocument from "./commands/UpdateDocument";
import Invoker from "./Invoker";
import Middleware from "./Middleware";
import JsonDAO from "./dao/JsonDAO";

Trigger = (newInvoker) => {
    let invoker = newInvoker;
}

Trigger.prototype.getInvoker = () => {
    return invoker;
}

Trigger.prototype.setInvoker = (newInvoker) => {
    invoker = newInvoker;
}

Trigger.prototype.addElement = (object) => {
    invoker.execute(new AddDocument(object,invoker.getMiddleware()))
}

Trigger.prototype.removeElement = (object) => {
    invoker.execute(new RemoveDocument(object,invoker.getMiddleware()));
}

Trigger.prototype.updateElement = (previousObject,object) => {
    invoker.execute(new UpdateDocument(previousObject,object,invoker.getMiddleware()));
}

Trigger.prototype.undo = () => {
    invoker.undo();
}

Trigger.prototype.redo = () => {
    invoker.redo();
}

Trigger.prototype.commit = () => {
    invoker.commit();
}

export default new Trigger(new Invoker(new Middleware(new JsonDAO())));
