export default Middleware = (newDao) => {
    let dao = newDao;
    let objects = []; 
}

Middlware.prototype.getDAO = () => {
    return dao;
}

Middleware.prototype.setDAO = (newDao) => {
    dao = newDao;
}

Middleware.prototype.getObjects = () => {
    return objects;
}

Middleware.prototype.setObjects = (newObjects) => {
    objects = newObjects;
}

Middleware.prototype.addDocument = (object) => {
    objects.push(object);
}

Middleware.prototype.removeDocument = (object) => {
    objects = objects.filter(o => o.id !== object.id)
}

Middleware.prototype.updateDocument = (object) => {
    previousObjects = object.filter(o => o.id !== object.id);
    objects = [ previousObjects, object ];
}

Middleware.prototype.commit = () => {
    dao.write(objects);
}
