Command = (newObject,newMiddleware) => {
    this._object = newObject;
    this._middleware = newMiddleware;
    if(this.constructor === Command){
        throw new Error("FYI: Instance of Abstract class cannot  be instantited.")
    }
}

Command.prototype.getObject = () => {
    return this._object;
}

Command.prototype.setObject = (newObject) => {
    this._object = newObject;
}

Command.prototype.getMiddleware = () => {
    return this._middleware;
}

Command.prototype.setMiddleware = (newMiddleware) => {
    this._middleware = newMiddleware;
}

export default Command;
